	$(function() {
		$(document).ready(function() {
			$('.datepicker')
			.datepicker({
				format: 'mm/dd/yyyy',
					changeMonth: true,
					changeYear: true
			});

			display_ct();

			$("#sqlquery").fseditor();
			//$('#fseditable').highlight({source:1, zebra:1, indent:'space', list:'ol',attribute: 'data-language'});

			var ce = document.getElementById("fseditable");
			// Probably needs more kludges to be portable... you get the idea
			var text = ce.textContent || ce.innerText;
			var sqleditor = CodeMirror(function(node){ce.parentNode.replaceChild(node, ce);}, {
			  value: text
			  /* , your options */
			});

			$(document).off("click","#checksql");
			$(document).on("click","#checksql",function() {
				$(".sqlerror").html('');
				//var sqlquery=$("#fseditable").val();
				var sqlquery=sqleditor.getValue();
				if(sqlquery==""){
					alert("Please enter sql query");
					$("#fseditable").focus();
				}else{
					$.ajax({
						url: '../assets/includes/checksql.inc.php',
						type: 'POST',
						data: { query: encodeURI(sqlquery) },
						success: function(result) {
							if (result != false)
							{
								var results = JSON.parse(result);
								if(results.error == "")
								{
									alert("OK");
								}else
									$(".sqlerror").html(results.error);

							}else{
								alert("Error in request. Please try again later.");
							}
						}
					});
				}

			});

			$(document).off("click","#runtest");
			$(document).on("click","#runtest",function() {
				$(".sqlerror").html('');
				var sqlquery=sqleditor.getValue();
				var emailtosend=$("#schemails").val();
				if(sqlquery==""){
					alert("Please enter sql query");
					$("#fseditable").focus();
				}else if(emailtosend==""){
					alert("Email required to send result csv");
					$("#schemails").focus();
				}else{
					$.ajax({
						url: '../assets/includes/sqltester.inc.php',
						type: 'POST',
						data: { email:encodeURI(emailtosend),query: encodeURI(sqlquery) },
						success: function(result) {
							if (result != false)
							{
								var results = JSON.parse(result);
								if(results.error == "")
								{
									alert("Test report will be soon send to given emails");
								}else
									$(".sqlerror").html(results.error);

							}else{
								alert("Error in request. Please try again later.");
							}
						}
					});
				}

			});

			/*$(document).on("click",".fullscreenicon",function() {
				if($( ".fullscreenicon" ).hasClass( "notfull" )==true){
					$("#sqlquery").addClass( "enlargeit" );
					$(".fullscreenicon").removeClass( "notfull" ).addClass( "onfull" );
				}else{
					$("#sqlquery").removeClass( "enlargeit" );
					$(".fullscreenicon").removeClass( "onfull" ).addClass( "notfull" );
				}
			});	*/

			//var codeText = $(".fs-editable")[0];
			/*var editor = CodeMirror.fromTextArea(sqlquery, {
				lineNumbers: true,
				textWrapping: true
			});*/

			/*$(document).on("click",".fs-icon",function() {
