	$(function() {
		$(document).ready(function() {
			$('.datepicker')
			.datepicker({
				format: 'mm/dd/yyyy',
					changeMonth: true,
					changeYear: true
			});
			display_ct();
			$("#sqlquery").fseditor();
			//$('#fseditable').highlight({source:1, zebra:1, indent:'space', list:'ol',attribute: 'data-language'});

			var ce = document.getElementById("fseditable");
			// Probably needs more kludges to be portable... you get the idea
			var text = ce.textContent || ce.innerText;
			var sqleditor = CodeMirror(function(node){ce.parentNode.replaceChild(node, ce);}, {
			  value: text
			  /* , your options */
			});




			$(document).off("click","#checksql");
			$(document).on("click","#checksql",function() {
				$(".sqlerror").html('');
				var sqlquery=sqleditor.getValue();
				$.ajax({
					url: '../assets/includes/checksql.inc.php',
					type: 'POST',
					data: { query: encodeURI(sqlquery) },
					success: function(result) {
						if (result != false)
						{
							var results = JSON.parse(result);
							if(results.error == "")
							{
								alert("OK");
							}else
								$(".sqlerror").html(results.error);

						}else{
							alert("Error in request. Please try again later.");
						}
					}
				});

			});

			$(document).off("click","#runtest");
			$(document).on("click","#runtest",function() {
				$(".sqlerror").html('');
				var sqlquery=sqleditor.getValue();
				var emailtosend=$("#schemails").val();
				if(sqlquery==""){
					alert("Please enter sql query");
					$("#fseditable").focus();
				}else if(emailtosend==""){
					alert("Email required to send result csv");
					$("#schemails").focus();
				}else{
					$.ajax({
						url: '../assets/includes/sqltester.inc.php',
						type: 'POST',
						data: { email:encodeURI(emailtosend),query: encodeURI(sqlquery) },
						success: function(result) {
							if (result != false)
							{
								var results = JSON.parse(result);
								if(results.error == "")
								{
									alert("Test report will be soon send to given emails");
								}else
									$(".sqlerror").html(results.error);

							}else{
								alert("Error in request. Please try again later.");
							}
						}
					});
				}

			});

			/*$(document).on("click",".fullscreenicon",function() {
				if($( ".fullscreenicon" ).hasClass( "notfull" )==true){
					$("#sqlquery").addClass( "enlargeit" );
					$(".fullscreenicon").removeClass( "notfull" ).addClass( "onfull" );
				}else{
					$("#sqlquery").removeClass( "enlargeit" );
					$(".fullscreenicon").removeClass( "onfull" ).addClass( "notfull" );
				}
			});*/

			$.widget("ui.dialog", $.extend({}, $.ui.dialog.prototype, {
				_title : function(title) {
					if (!this.options.title) {
						title.html("&#160;");
					} else {
						title.html(this.options.title);
					}
				}
			}));

			$("#edit-dialog-message-sc").dialog({
				autoOpen : true,
				modal : true,
				width: "auto",
				title : "<div class='widget-header'><h4><i class='icon-ok'></i>Edit Scheduled Report</h4></div>",
				close: function() {
					$("#edit-dialog-message-sc").dialog("close");
					$("#edit-dialog-message-sc").remove();
					parent.$("#response").html("");
				}
				/*buttons : [{
					html : "Cancel",
					"class" : "btn btn-default",
					click : function() {
						$(this).dialog("close");
					}
				}, {
					html : "<i class='fa fa-check'></i>&nbsp; OK",
					"class" : "btn btn-primary",
					click : function() {
						$(this).dialog("close");
					}
				}]*/

			});

			$(document).off("click","#edit-scheduled-report-cancel");
			$(document).on("click","#edit-scheduled-report-cancel",function() {
				$("#edit-dialog-message-sc").dialog("close");
				$("#edit-dialog-message-sc").remove();
				$("#response").html("");
			});

			var $checkoutForm = $('#edit-checkout-form').validate({
			// Rules for form validation
				rules : {
					schname : {
						required : true
					},
					schstatus : {
						required : true
					},
					lastrunstatus : {
						required : true
					},
					schcreatedby : {
						required : true
					},
					schdate : {
						required : true
					},
					schemails : {
						required : true
					},
					sqlquery : {
						required : true
					},
					schdesc : {
						required : true
					},
					schmin : {
						required : true
					},
					schhour : {
						required : true
					},
					schday : {
						required : true
					},
					schmonth : {
						required : true
					},
					schweekday : {
						required : true
					}
				},

				// Messages for form validation
				messages : {
					schname : {
						required : 'Please enter Scheduled Report Name'
					},
					schstatus : {
						required : 'Please select Status'
